CREATE TABLE student (id BIGINT NOT NULL AUTO_INCREMENT PRIMARY KEY, name VARCHAR(250), birthday DATE, groupnumber INT);
CREATE TABLE subject (id BIGINT NOT NULL AUTO_INCREMENT PRIMARY KEY, name VARCHAR(250),description VARCHAR(50), grade INT);
CREATE TABLE paymenttype (id BIGINT NOT NULL AUTO_INCREMENT PRIMARY KEY, name VARCHAR(250));
CREATE TABLE payment (id BIGINT NOT NULL AUTO_INCREMENT PRIMARY KEY, type_id BIGINT, FOREIGN KEY (type_id) REFERENCES paymenttype(id), amount DECIMAL, student_id BIGINT NOT NULL, FOREIGN KEY (student_id) REFERENCES student(id), payment_date DATETIME);
CREATE TABLE mark (id BIGINT NOT NULL AUTO_INCREMENT PRIMARY KEY, student_id BIGINT NOT NULL, FOREIGN KEY(student_id) REFERENCES student(id), subject_id BIGINT NOT NULL, FOREIGN KEY(subject_id) REFERENCES subject(id), mark INT);